import './App.css';
import { useState } from 'react';
import imagemDayZ from './assets/dayz.jpg';
import imagemRust from './assets/Rust.jpg';


export default function App() {
  let imagemJogo = "";
  let informacaoJogo = "";
  let nomeJogo = "DayZ";

  const [tipoDoComponeteCard, setTipoDoComponeteCard] = useState("DayZ");

  const alterarState = () =>{
    if (tipoDoComponeteCard === "DayZ"){
      setTipoDoComponeteCard("Rust");
    }else{
      setTipoDoComponeteCard("DayZ")
    }
  };

  if (tipoDoComponeteCard === "DayZ"){
    imagemJogo = imagemDayZ;
    informacaoJogo = "Jogo de sobrevivência e multijogador. Uma terra arrasada por zumbis infectados,onde você compete com outros sobreviventes por recursos limitados. Você vai ajudar estranhos em sua luta? Ou evitará traições como um lobo solitário? Esse é o DayZ. Essa é a sua história.";
    nomeJogo = "DayZ"
  }else{
    imagemJogo = imagemRust;
    informacaoJogo = "Jogo de sobrevivência e multijogador.O único objetivo em Rust é sobreviver. Tudo quer que você morra - a vida selvagem da ilha e outros habitantes, o meio ambiente, outros sobreviventes. Faça o que for preciso para durar mais uma noite.";
    nomeJogo = "Rust"
  }


  return (
    <div className="App">
      {/*componete topo*/}
      <header>
      <h1>Projeto Props + useState</h1>
        <p>Clique no botão para mudar os componentes abaixo</p>
        <button onClick={alterarState}>Mudar Jogo</button>
      </header>

      {/*componete CardJogo*/}
      <div id='CardJogo'>
        <img src={imagemJogo} alt="Jogo"/>
      </div>

      {/*componente CardInformações*/}
      <div className='CardInformacao'>
        <h3>Informação sobre o {nomeJogo} </h3>
        <p>{informacaoJogo}</p>
      </div>
    </div>
  );
}
