import './App.css';
import { useState } from 'react';
import CardJogo from './componetes/CardJogo/cardJogo';
import Cardinformacao from './componetes/CardInformacoes/Cardinformacao';


export default function App() {
  let imagemJogo = "";
  let informacaoJogo = "";
  let nomeJogo = "DayZ";

  const [tipoDoComponeteCard, setTipoDoComponeteCard] = useState("DayZ");

  const alterarState = () =>{
    if (tipoDoComponeteCard === "DayZ"){
      setTipoDoComponeteCard("Rust");
    }else{
      setTipoDoComponeteCard("DayZ")
    }
  };

  return (
    <div className="App">
      {/*componete topo*/}
      <header>
      <h1>Projeto Props + useState</h1>
        <p>Clique no bot√£o para mudar os componentes abaixo</p>
        <button onClick={alterarState}>Mudar Jogo</button>
      </header>  
      <CardJogo nomeJogo = {tipoDoComponeteCard}/>
      <Cardinformacao nomeJogo = {tipoDoComponeteCard}/>
    </div>
  );
}
