import './App.css';
import { useState } from 'react';
import imagemDayZ from '../public/assets/dayz.jpg';
import imagemRust from '../public/assets/Rust.jpg';


export default function App() {
  let imagemJogo = "";
  let informacaoJogo = "";
  let nomeJogo = "DayZ";

  const [tipoDoComponeteCard, setTipoDoComponeteCard] = useState("DayZ");

  const alterarState = () =>{
    if (tipoDoComponeteCard === "DayZ"){
      setTipoDoComponeteCard("Rust");
    }else{
      setTipoDoComponeteCard("DayZ")
    }
  };

  if (tipoDoComponeteCard === "DayZ"){
    imagemJogo = imagemDayZ;
    informacaoJogo = "Jogo de sobrevivencia, multijogador. Uma terra arrasada por zumbis infectados,onde você compete com outros sobreviventes por recursos limitados. Você vai ajudar estranhos em sua luta? Ou evitará traições como um lobo solitário? Esse é o DayZ. Essa é a sua história.";
    nomeJogo = "DayZ"
  }else{
    imagemJogo
  }


  return (
    <div className="App">

    </div>
  );
}
