import './style.css';

export default function(props){

    informacaoJogo = "Jogo de sobrevivência e multijogador. Uma terra arrasada por zumbis infectados,onde você compete com outros sobreviventes por recursos limitados. Você vai ajudar estranhos em sua luta? Ou evitará traições como um lobo solitário? Esse é o DayZ. Essa é a sua história.";
    nomeJogo = "DayZ"
    return(
        <div className='CardInformacao'>
        <h3>Informação sobre o {props.nomeJogo} </h3>
        <p>{props.informacaoJogo}</p>
      </div>
    )
}
